import React, { Component } from "react";
import { BrowserRouter ,NavLink, Switch, Route } from "react-router-dom";
import Navbar from 'react-bootstrap/Navbar'
import logo from "../img/Logo ODCVT.svg"
import avatar from "../img/avatar.svg"
import { Redirect } from "react-router-dom";
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
const activeStyle = {

    borderBottom: '4px solid #FF7900',
    borderBottomWidth: 5

};



class Login extends Component {

  constructor(props) {
    super(props);
    this.state = {
      islogged: false,
      loginParams: {
        user_id: "",
        user_password: ""
      }
    };
  }
  handleFormChange = event => {
    let loginParamsNew = { ...this.state.loginParams };
    let val = event.target.value;
    loginParamsNew[event.target.name] = val;
    this.setState({
      loginParams: loginParamsNew
    });
  };

  login = event => {
    let user_id = this.state.loginParams.user_id;
    let user_password = this.state.loginParams.user_password;
    let user_role = "admin";
    let user_role2 = "presse";
    let pw= "12AiLneIs!O893";
    let pw2="I12aNESli!O792"

    if ((user_id === "admin" && user_password === pw)|| (user_id === "presse" && user_password ===pw2) )  {

      if (user_id === "admin") {
        localStorage.setItem("role", user_role);
      } else {
        localStorage.setItem("role", user_role2);
      }
      localStorage.setItem("token", "T");
      localStorage.setItem("userId", user_id );


      this.setState({
        islogged: true
      });
    }

    event.preventDefault();
  };
  render() {
    if ((localStorage.getItem("role") ==="admin") || (localStorage.getItem("role") ==="presse")){
      return(<Redirect to="/" />) 
    }
    return ( 
    <div>
     <Navbar  style={{width: '1920px',height: '100px'}}   className="navbarcolor " variant="dark">

      <Navbar.Brand style = {{ marginLeft: '327px',width: '50px',height: '100px' , marginTop:'25px'}} href="#home" >


        <img
          alt=""
          src={logo}
          //width="100"
          //height="60"
          className="d-inline-block align-top"
        />{' '}



      </Navbar.Brand>













        <div style={{marginLeft: '1000px',marginBottom:'-76px'}} >


        {/* style={{color:'white', cursor : 'pointer', fontFamily:'HelveticaNeue-Bold' ,fontSize:'19px' }} */}
        <NavLink to="/"  style={{color:'#FF7900', cursor : 'pointer', fontFamily:'HelveticaNeue-Bold' ,fontSize:'16px' , borderBottom: '4px solid #FF7900',
borderBottomWidth: 5 }}  >EN</NavLink>&nbsp; &nbsp; 
     <NavLink to="/"   style={{color:'white', cursor : 'pointer', fontFamily:'HelveticaNeue-Bold' ,fontSize:'16px' }} >FR</NavLink>
     &nbsp; &nbsp;  

      <button  style={{border:'none', background:'none' }} onClick={() => {
           // localStorage.clear();
     // history.push("/login");
        //window.location.reload(false);
    }}   > 
      <img
style={{marginBottom:'20px'}}
          alt=""
          src={avatar}

          //width="100"
          //height="60"
         // className="d-inline-block align-top"
        />

     </button> 


        </div>  






  </Navbar>

    <div  style={{marginLeft: '800px',marginRight:'800px', marginTop:'175px'}} >

    <div style={{height: '74px', width:'310px' }} > 
   <h5 style={{fontFamily: 'HelveticaNeue-Bold'  ,fontSize:'34px',letterSpacing:'-0.68px;'}}>
   {/* //bold -20 */}
   Orange Digital Center Virtual Tour</h5> 

    </div>
    &nbsp; &nbsp; 


    <div style={{width: '320px',height: '102px' }} > 
    <p style={{fontFamily:'Helvetica Neue' ,fontSize:'16px',letterSpacing:'-0.32px'}}> Excepteur sint occaecat cupidatat non proident, 
sunt in culpa qui officia deserunt mollit anim id est laborum
 duis aute irure dolor in reprehenderit in voluptate velit esse 
 cillum dolore eu fugiat nulla pariatur</p>

    </div>

  <br/>
  <br/>
  <br/>
<div>
  <form>
                <div className="form-group text-left">
                <label style={{fontWeight:'bold', fontSize:'16px'}}>Email </label>
                <input  
                      style={{width:'320px', height:'40px'}} type="email" className="form-control" placeholder="Enter email"
                />

                </div>
                <div className="form-group text-left">
                    <label style={{fontWeight:'bold', fontSize:'16px'}}>Password</label>
                    <input type="password" 
                    style={{width:'320px', height:'40px'}}  className="form-control"


                        placeholder="Password"
                    />
                </div>

                <button   style = {{background:'#FF7900', fontWeight:'bold',fontSize:'16px',color:"black",border:"20px",width:'320px', height:'40px', borderRadius: "1px"}}   className="btn btn-primary btn-block">Login</button>

            </form>
        </div>

    </div>

  </div>

    );
  }
}
export default Login;
