import React, { useState } from "react";
import { BrowserRouter ,NavLink, Switch, Route } from "react-router-dom";
import Navbar from 'react-bootstrap/Navbar'
import logo from "../img/Logo ODCVT.svg"
import avatar from "../img/avatar.svg"
import { Redirect } from "react-router-dom";
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import './login.css'
import axios from "axios";
const activeStyle = {

    borderBottom: '4px solid #FF7900',
    borderBottomWidth: 5

};



export default function Login  (props) {
  const [state , setState] = useState({
    email : "",
    password : ""
})
const handleChange = (e) => {
    const {id , value} = e.target   
    setState(prevState => ({
        ...prevState,
        [id] : value
    }))
}

const handleSubmitClick = (e) => {
  e.preventDefault();
  const payload={
      "email":state.email,
      "password":state.password,
  }
  axios.post('https://virtuelodc-backend.herokuapp.com/'+'login', payload)
      .then(function (response) {
          if(response.data.code === 200){
              setState(prevState => ({
                  ...prevState,
                  'successMessage' : 'Login successful. Redirecting to home page..'
              }))
              //redirectToHome();
              props.showError(null)
          }
          else if(response.data.code === 204){
              props.showError("Username and password do not match");
          }
          else{
              props.showError("Username does not exists");
          }
      })
      .catch(function (error) {
          console.log(error);
      });
}

    const [anchorEl, setAnchorEl] = React.useState(null);

    const handleClick = (event) => {
      setAnchorEl(event.currentTarget);
    };

    const handleClose = () => {
      setAnchorEl(null);
    };
    return ( 
    <div>
     <Navbar  style={{width: '1920px',height: '100px'}}   className="navbarcolor " variant="dark">

      <Navbar.Brand style = {{ marginLeft: '327px',width: '50px',height: '100px' , marginTop:'25px'}} href="#home" >


        <img
          alt=""
          src={logo}
          //width="100"
          //height="60"
          className="d-inline-block align-top"
        />{' '}



      </Navbar.Brand>













        <div style={{marginLeft: '1000px',marginBottom:'-76px'}} >


        {/* style={{color:'white', cursor : 'pointer', fontFamily:'HelveticaNeue-Bold' ,fontSize:'19px' }} */}
        <NavLink to="/"  style={{color:'#FF7900', cursor : 'pointer', fontFamily:'HelveticaNeue-Bold' ,fontSize:'16px' , borderBottom: '4px solid #FF7900',
borderBottomWidth: 5 }}  >EN</NavLink>&nbsp; &nbsp; 
     <NavLink to="/"   style={{color:'white', cursor : 'pointer', fontFamily:'HelveticaNeue-Bold' ,fontSize:'16px' }} >FR</NavLink>
     &nbsp; &nbsp;  


      <Button  aria-controls="simple-menu" aria-haspopup="true" onClick={handleClick}>
      <img
      style={{marginBottom:'20px'}}
          alt=""
          src={avatar}

          //width="100"
          //height="60"
         // className="d-inline-block align-top"
        />
      </Button>

      <Menu
        id="simple-menu"
        anchorEl={anchorEl}
        //keepMounted
         open={Boolean(anchorEl)}
         onClose={handleClose}
         anchorOrigin={{ vertical: "bottom", horizontal: "center" }}
         transformOrigin={{ vertical: "top", horizontal: "center" }}
         >
        <MenuItem onClick={handleClose}>Logout</MenuItem>

      </Menu>
    </div>








  </Navbar>

    <div  style={{marginLeft: '800px',marginRight:'800px', marginTop:'175px'}} >

    <div style={{height: '74px', width:'310px' }} > 
   <h5 style={{fontFamily: 'HelveticaNeue-Bold'  ,fontSize:'34px',letterSpacing:'-0.68px;'}}>
   {/* //bold -20 */}
   Orange Digital Center Virtual Tour</h5> 

    </div>
    &nbsp; &nbsp; 


    <div style={{width: '320px',height: '102px' }} > 
    <p style={{fontFamily:'Helvetica Neue' ,fontSize:'16px',letterSpacing:'-0.32px'}}> Excepteur sint occaecat cupidatat non proident, 
sunt in culpa qui officia deserunt mollit anim id est laborum
 duis aute irure dolor in reprehenderit in voluptate velit esse 
 cillum dolore eu fugiat nulla pariatur</p>

    </div>

  <br/>
  <br/>
  <br/>
<div>
  <form>
                <div className="form-group text-left">
                <label style={{fontWeight:'bold', fontSize:'16px'}}>Email </label>
                <input  
                  value={state.email}
                       onChange={handleChange}
               type="email" className="form2" placeholder="Enter email"
                />

                </div>
                <div className="form-group text-left">
                    <label style={{fontWeight:'bold', fontSize:'16px'}}>Password</label>
                    <input type="password" 
                       className="form2"

                       value={state.password}
                        onChange={handleChange} 
                        placeholder="Password"
                    />
                </div>

                <button   style = {{background:'#FF7900', fontWeight:'bold',fontSize:'16px',color:"black",border:"20px",width:'320px', height:'40px', borderRadius: "1px"}}   className="btn btn-primary btn-block"  onClick={handleSubmitClick}>Login</button>

            </form>
        </div>

    </div>

  </div>

    );
  }
