import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import CloseOutlinedIcon from '@material-ui/icons/CloseOutlined';
import { withStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import Select from '@material-ui/core/Select';
import Modal from '@material-ui/core/Modal';

import Button from '@material-ui/core/Button';
import MenuItem from '@material-ui/core/MenuItem';
import Box from '@material-ui/core/Box';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import TableContainer from '@material-ui/core/TableContainer';
import TableSortLabel from '@material-ui/core/TableSortLabel';

import UnfoldMoreIcon from '@material-ui/icons/UnfoldMore';
import './table.css'
import GradeIcon from '@material-ui/icons/Grade';

import Paper from '@material-ui/core/Paper';

import axios from 'axios';
const styles = theme => ({



 test:{
        fontFamily:'Helvetica Neue',
     color:"red"
 },
  root: {
       fontFamily:'Helvetica Neue',
      marginLeft:'200px',
     width: '80%',
    marginTop:' 90px',
    overflowX: 'auto',
  },
  table: {
         fontFamily:'Helvetica Neue',
    minWidth: 100,
  },
  arrowDropUp:{
      color:'#000000'
  },

});




function SimpleTable(props) {
  const { classes } = props;
   const [open, setOpen] = React.useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);
    const baseUrl = 'https://fakestoreapi.com/users'
  const [users, setUsers] = useState([]);
  const [FiltredUsers, setFiltredUsers] = useState([]);
 const [age, setAge] = React.useState('');

  const handleChange = (event) => {
    setAge(event.target.value);
  };

  const [sortOrder, setSortOrder] = useState('asc');
 const DropdownIndicator = (props) => (
        <div {...props.innerProps} className={classes.dropdown}>
            <UnfoldMoreIcon   className={classes.arrowDropUp}/>

        </div>
    );
  useEffect(() => {
    axios.get(baseUrl).then(result => {
      setUsers(result.data);
      setFiltredUsers(result.data)
      // console.log('test',result.data);


    })
  }, []);

  return (
    <Paper className={classes.root}>
      <TableContainer component={Paper} >

        <Table sx={{ minWidth: 650 }} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell   >
                <TableSortLabel
               // style={{ color:"#000000"}}

                 style={{fontWeight:'bold', fontSize:'15px'}}
                   IconComponent={DropdownIndicator}
                  active={true}
                  direction={sortOrder}
                  //onClick={() => requestSort()}
                >
           Session Information
                </TableSortLabel>
              </TableCell>
              <TableCell align="center">
              <TableSortLabel
       style={{fontWeight:'bold', fontSize:'15px'}}
                  active={true}
                  direction={sortOrder}
                   IconComponent={DropdownIndicator}
                 // onClick={() => requestSort2()}
                >
                 Full Name
                </TableSortLabel>
                </TableCell>
              <TableCell align="center"

              > 
               <TableSortLabel
               style={{fontWeight:'bold', fontSize:'15px'}}
            IconComponent={DropdownIndicator}
                  active={true}
                  direction={sortOrder}
                 // onClick={() => requestSort2()}
                >
                Profession
                </TableSortLabel>

